# -*- coding: UTF-8 -*-
from django.contrib import admin
from audiotracks.models import get_track_model, Artist, Genre, Playlist, Comment, Like

class CommentInline(admin.StackedInline):
    model = Comment
    extra = 1

class TrackAdmin(admin.ModelAdmin):
    list_display = ["title", "artist", "genre"]
    search_fields = ["artist", "name", "genre"]
    inlines = [CommentInline]


class ArtistAdmin(admin.ModelAdmin):
    list_display = ["name"]
    search_fields = ["name"]


class GenreAdmin(admin.ModelAdmin):
    list_display = ["name"]
    search_fields = ["name"]


class PlaylistAdmin(admin.ModelAdmin):
    list_display = ["name", "user"]
    search_fields = ["user", "name"]


class CommentAdmin(admin.ModelAdmin):
    list_display = ["text", "user", "track"]
    search_fields = ["user", "track", "text"]


class LikeAdmin(admin.ModelAdmin):
    list_display = ["track", "user"]
    search_fields = ["track", "user"]

admin.site.register(get_track_model(), TrackAdmin)
admin.site.register(Artist, ArtistAdmin)
admin.site.register(Genre, GenreAdmin)
admin.site.register(Playlist, PlaylistAdmin)
admin.site.register(Comment, CommentAdmin)
admin.site.register(Like, LikeAdmin)